    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String direccion = jTxt_direccion.getText();
        String email = jTxt_email.getText();
        String nombre = jTxt_nombre.getText();
        String telefono = jTxt_telefono.getText();
        
        Conexion objetoConexion = new Conexion();
        String consulta = "INSERT INTO DJS_EDIFICIO (NOM_EDIF,DIRECCION_EDIF,TELEFONO_EDIF,CORREO_EDIF) VALUES (?,?,?,?)";
        
        try {
            CallableStatement cs=objetoConexion.establecerConexion().prepareCall(consulta);
                cs.setString(1, nombre);
                cs.setString(2, direccion);
                cs.setString(3, telefono);
                cs.setString(4, email);
                cs.execute();
                
                JOptionPane.showMessageDialog(null, "Registro agregado");
        } catch (Exception e) {
             JOptionPane.showMessageDialog(null, "No se pudo agregar el registro");
        }
    
    }//GEN-LAST:event_jButton1ActionPerformed

